const apikey = "35fa0da2f820dab55cf89ed370159069";
const apiUrl ="https://api.openweathermap.org/data/2.5/weather?&units=metric&q="

const searchBox = document.querySelector('.search input');
const searchBtn = document.querySelector('.search button');
const weatherIcon = document.querySelector('.weather-icon');

async function checkWeather(city){
    const response = await fetch(
        apiUrl+city+`&appid=${apikey}`
    )
    var data = await response.json();
    document.querySelector('.weather').style.display="flex"
    document.querySelector('.city').innerHTML=data.name;
    document.querySelector('.temp').innerHTML=Math.round(data.main.temp)+"c";
    document.querySelector('.wind').innerHTML=data.wind.speed+"km/h";

    if(data.weather[0].main == "Clouds"){
        weatherIcon.src="data:image/jpeg;base64,"
    }else if(data.weather[0].main == "Rain"){
        weatherIcon.src="data:image/jpeg;base64,"
    }else if(data.weather[0].main == "Drizzle"){
        weatherIcon.src="data:image/png;base64,"
    }else if(data.weather[0].main == "Mist"){
        weatherIcon.src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTu7_qT9lgL6lmMBZQZr9WmxiSXElx2nUkH2Q&usqp=CAU"
    }

    

}
searchBtn.addEventListener("click",()=>{
    checkWeather(searchBox.value)
})